import axios from 'axios';
import {useHistory, useParams} from 'react-router-dom';

const TransactionPage = () => {
    const history = useHistory();
    const {standId, transactionId} = useParams();
    const [lemonades, setLemonades] = 0;
    const [transaction, setTransaction] = setState({})

    const goToInvoice = (e) => {
        e.preventDefault();

        axios.get(`http://localhost:8000/api/transactions/${transactionId}`)
        .then((res)=> setTransaction(res.data))
        .catch((err)=>console.log(res.err))

        const updatedTransaction = {
            ...transaction,
            stand_session: standId,
            lemonades: lemonades 
        }

        axios.put(`http://localhost:8000/api/transactions/${transactionId}`, updatedTransaction)
        .then((res) => {
            console.log(res);
            setTransaction(updatedTransaction)
        })
        .call((err) => console.log(err))

        history.push(`/invoice-page/${standId}/${transactionId}`)
    }

    return (
        <div>
            <form className="form" style={{margin:"25px", padding:"15px", border:"solid black 2px"}} onSubmit={(e)=>goToInvoice()}>
                <h2>Number of Lemonades for Order</h2>
                <input type="number" style={{marginBottom:"15px"}}/><br />
                <button className="btn btn-primary" onChange={(e)=>setLemonades(e.target.value)}>Create Invoice</button>
            </form>
        </div>
    )
}

export default TransactionPage;
